
public class Ej1App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int A = 500;
		int B = 50;
		
		if (A<B) {
			System.out.println("El valor mayor es la variable B");
		} else {
			if(A>B) {
				System.out.println("El valor mayor es la variable A");
			} else {
				if(A==B) {
					System.out.println("Las variables tienen el mismo valor");

				}
			}
		}
		
	}

}
